import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Zone = () => {
  const [zones, setZones] = useState([]);
  const [nom, setNom] = useState('');
  const [villeId, setVilleId] = useState('');

  useEffect(() => {
    fetchZones();
  }, []);

  const fetchZones = async () => {
    try {
      const response = await axios.get('/api/zones'); // Replace with your API endpoint
      setZones(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const createZone = async () => {
    try {
      await axios.post('/api/zones', { nom, villeId }); // Replace with your API endpoint
      fetchZones();
      setNom('');
      setVilleId('');
    } catch (error) {
      console.error(error);
    }
  };

  const deleteZone = async (id) => {
    try {
      await axios.delete(`/api/zones/${id}`); // Replace with your API endpoint
      fetchZones();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Zones</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom</th>
            <th>Ville ID</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {zones.map((zone) => (
            <tr key={zone.id}>
              <td>{zone.id}</td>
              <td>{zone.nom}</td>
              <td>{zone.villeId}</td>
              <td>
                <button onClick={() => deleteZone(zone.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Create Zone</h2>
      <form onSubmit={createZone}>
        <label>
          Nom:
          <input type="text" value={nom} onChange={(e) => setNom(e.target.value)} />
        </label>
        <br />
        <label>
          Ville ID:
          <input type="text" value={villeId} onChange={(e) => setVilleId(e.target.value)} />
        </label>
        <br />
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default Zone;
