import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Modal from "react-modal";

const Zone = ({ zoneId }) => {
  const [zones, setZones] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedZone, setSelectedZone] = useState(null);
  const [cities, setCities] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await axios.get(`/api/zones`); // Use axios.get instead of axios
        setZones(result.data);
      } catch (error) {
        console.error("Error fetching zones:", error);
      }
    };
    fetchData();
  }, [zoneId]);

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const result = await axios.get(`/api/cities`); // Use axios.get instead of axios
        setCities(result.data);
      } catch (error) {
        console.error("Error fetching cities:", error);
      }
    };
    fetchCities();
  }, []);

  const handleDelete = (zoneId) => {
    if (window.confirm("Are you sure you want to delete this zone?")) {
      axios
        .delete(`/api/zones/${zoneId}`)
        .then(() => {
          setZones(zones.filter((zone) => zone.id !== zoneId));
        })
        .catch((error) => {
          console.error("Error deleting zone:", error);
        });
    }
  };

  const handleOpenModal = (zone) => {
    setSelectedZone(zone);
    setModalIsOpen(true);
  };

  const handleCloseModal = () => {
    setSelectedZone(null);
    setModalIsOpen(false);
  };

  const handleSave = () => {
    // TODO: handle save logic
    handleCloseModal();
  };

  return (
    <div>
      <h2>Zones</h2>
      <Link to={`/create-zone`} className="btn btn-primary">
        Add Zone
      </Link>

      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>City</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {zones.map((zone) => (
            <tr key={zone.id}>
              <td>{zone.nom}</td>
              <td>{zone.ville}</td>
              <td>
                <button
                  className="btn btn-secondary me-2"
                  onClick={() => handleOpenModal(zone)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger"
                  onClick={() => handleDelete(zone.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Modal isOpen={modalIsOpen} onRequestClose={handleCloseModal}>
        <h3>Modification de la zone</h3>
        <ul>
          <li>
            <label>Nom de la zone:</label>
            <input
              type="text"
              value={selectedZone && selectedZone.name} // Use value instead of text
              onChange={(e) =>
                setSelectedZone({ ...selectedZone, name: e.target.value })
              } // Handle input changes
            />
          </li>

                    <li>
                        <label>Ville:</label>
                        <select value={selectedZone && selectedZone.city && selectedZone.city.id}>
                            {cities.map((city) => (
                                <option key={city.id} value={city.id}>
                                    {city.name}
                                </option>
                            ))}
                        </select>
                    </li>
                </ul>
                <button className="btn btn-primary" onClick={handleCloseModal}>
                    Annuler
                </button>
                <button className="btn btn-success" onClick={handleSave}>
                    Sauvegarder
                </button>
            </Modal>

        </div>
    );
};

export default Zone
