import React, { useState, useEffect } from 'react';
import axios from 'axios';
const Zone = () => {
const [zones, setZones] = useState([]);
const [zoneName, setZoneName] = useState('');
const [zoneCity, setZoneCity] = useState('');
const [zoneId, setZoneId] = useState('');
const [showModal, setShowModal] = useState(false);
const [cities, setCities] = useState([]);

useEffect(() => {
fetchZones();
}, []);



const fetchZones = async () => {
try {
const response = await axios.get('/api/Zones');
setZones(response.data);
} catch (error) {
console.error(error);
}
};

const handleInputChange = (event) => {
const { nom, value } = event.target;
if (nom === 'zoneName') {
setZoneName(value);
} else if (nom === 'zoneCity') {
setZoneCity(value);
}
};

const handleAddZone = async () => {
try {
const response = await axios.post('/api/zones/save', { nom:
zoneName, city: zoneCity });
setZones([...zones, response.data]);
setShowModal(false);
} catch (error) {
console.error(error);
}
};

const handleEditZone = async (id) => {
try {
const response = await axios.put(`/api/zones/${id}`, { nom:
zoneName, city: zoneCity });
const updatedZones = zones.map((zone) => {
if (zone.id === id) {
return response.data;
}
return zone;
});
setZones(updatedZones);
setShowModal(false);
} catch (error) {
console.error(error);
}
};


const handleDeleteZone = async (id) => {
try {
await axios.delete(`/api/zones/${id}`);
const updatedZones = zones.filter((zone) => zone.id !== id);
setZones(updatedZones);
} catch (error) {
console.error(error);
}
};


const handleOpenModal = (zone) => {
if (zone) {
setZoneId(zone.id);
setZoneName(zone.nom);
setZoneCity(zone.ville.nom);
} else {
setZoneId('');
setZoneName('');
setZoneCity('');
}
setShowModal(true);
};


const handleCloseModal = () => {
setShowModal(false);
};


return (
<div>
<h1>Zone Management</h1>
<button className="btn btn-primary mb-3" onClick={() =>
handleOpenModal()}>
Add Zone
</button>
<table className="table">
<thead>
<tr>
<th>Name</th>
<th>City</th>
<th>Actions</th>
</tr>
</thead>
<tbody>
{zones.map((zone) => (
<tr key={zone.id}>
<td>{zone.nom}</td>
<td>{zone.ville.nom}</td>
<td>
<button className="btn btn-secondary me-2"
onClick={() => handleOpenModal(zone)}>
Edit
</button>
<button className="btn btn-danger"
onClick={() => handleDeleteZone(zone.id)}>
Delete
</button>
</td>
</tr>
))}
</tbody>
</table>
</div>
)
};
export default Zone;
