import React, { useState, useEffect } from "react";
import axios from "axios";

const ZoneByCity = () => {
  const [zones, setZones] = useState([]);
  const [cities, setCities] = useState([]);
  const [selectedCityNom, setSelectedCityNom] = useState("");
  const [selectedZoneNom, setSelectedZoneNom] = useState("");

  useEffect(() => {
    axios.get("api/villes").then((response) => {
      setCities(response.data);
    });
  }, []);

  const handleCityChange = (event) => {
    const cityNom = event.target.value;
    setSelectedCityNom(cityNom);

    axios.get(`/zone/api/villes/${cityNom}/zones`).then((response) => {
      setZones(response.data);
    });
  };
  const handleZoneChange = (event) => {
    const zoneNom = event.target.value;
    setSelectedZoneNom(zoneNom);

    axios.get(`/zone/api/villes/${zoneNom}/zones`).then((response) => {
      setZones(response.data);
    });
  };

  return (
    <div>
      <h2>Zone par ville</h2>
      <div className="form-group">
        <label htmlFor="cityId">Select a city:</label>
        <select
          className="form-control"
          id="cityId"
          value={selectedCityNom}
          onChange={handleCityChange}
        >
          <option value="">All cities</option>
          {cities.map((city) => (
            <option key={city.id} value={city.id}>
              {city.nom}
            </option>
          ))}
        </select>
        </div>
        <div className="form-group">
        <label htmlFor="cityId">Select a zone:</label>
        <select
          className="form-control"
          id="cityId"
          value={selectedZoneNom}
          onChange={handleZoneChange}
        >
           <option value="">All zones</option>
          {zones.map((zone) => (
            <option key={zone.id} value={zone.id}>
              {zone.nom}
            </option>
          ))}
        </select>
      </div>
      <div className="form-group">
        <label htmlFor="cityId">Select Type Garde:</label>
        <select
          className="form-control"
          id="cityId">
           <option value="">Jour</option>
           <option value="">Nuit</option>
        </select>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>City</th>
          </tr>
        </thead>
        <tbody>
          {zones.map((zone) => (
            <tr key={zone.id}>
              <td>{zone.nom}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ZoneByCity;
